INSERT INTO `booking_db`.`review_tours` (`id_user`, `id_tour`, `score`, `content`, `create_time`) VALUES (115, 13, 10.0, 'Tour chất lượng tốt, HDV nhiệt tình. lái xe cũng giỏi, nhưng hôm đoàn chúng tôi đi xe 16c nên là ngồi kế những khách ghép lẻ khác thấy ko tiện, ko thoải mái cho lắm. Công ty nên xem xét cải thiện xe tốt hơn.', '2020-10-23 00:00:00');
INSERT INTO `booking_db`.`review_tours` (`id_user`, `id_tour`, `score`, `content`, `create_time`) VALUES (116, 13, 10.0, '- DỊCH VỤ VÀ CHƯƠNG TRÌNH KHÁ ỔN, TỦY KO THỂ BẰNG ĐƯỢC CÁC DỊCH VỤ VÙNG KHÁC. NHƯNG CĂN BẢN VÙNG MIỀN TÂY BẮC CHỈ CÓ NHƯ VẬY, NÊN MÌNH KHÁ HÀI LÒNG VÀ THOẢI MÁI KHI THAM GIA TOUR CÙNG CÁC BẠN.', '2020-10-09 00:00:00');
INSERT INTO `booking_db`.`review_tours` (`id_user`, `id_tour`, `score`, `content`, `create_time`) VALUES (117, 13, 10.0, '- Cảm ơn Ivivu tôi rất hài lòng với chuyến đi. Bạn nvien tư vấn tour rất nhiệt tình và chu đáo. Tôi đăng ký cả 2 chuyến đi và có trải nghiệm rất tuyệt cùng các bạn.', '2020-10-09 00:00:00');
INSERT INTO `booking_db`.`review_tours` (`id_user`, `id_tour`, `score`, `content`, `create_time`) VALUES (118, 14, 10.0, 'Nên đưa thông tin chi tiết hơn về hành trình (thời gian xuất phát, số km di chuyển, địa điểm tham quan) để khách hình dung tốt hơn về chuyến đi của mình. Làm việc với các đối tác nhà hàng để họ hiểu hơn về đối tượng khách du lịch vì đây là dịp tốt để tự quảng cáo cho nhà hàng của mình, như nhà hàng lẩu ở Đồng Văn làm khá tốt là đích thân chủ quán ra bàn giới thiệu về món ăn, về các loại rau đặc biệt của địa phương.... trong khi nhà hàng Ngon ở Tp. Cao Bằng hỏi nhân viên món này món gì họ cứ ngơ ra (?!). Cần thêm các điểm bán sản phẩm địa phương', '2020-11-25 00:00:00');
