INSERT INTO `booking_db`.`review_tours` (`id_user`, `id_tour`, `score`, `content`, `create_time`) VALUES (121, 14, 10.0, 'Tour đã tư vấn rõ ràng chi tiết. HDV Kiên nhiệt tình năng nổ. Tài xế Tú lái xe đường đèo an toàn cẩn thận. Đoàn chúng tôi chấm điểm 10 cho chất lượng chuyến đi.', '2020-11-04 00:00:00');
INSERT INTO `booking_db`.`review_tours` (`id_user`, `id_tour`, `score`, `content`, `create_time`) VALUES (122, 14, 8.0, 'chương trình đi vui, HDV trẻ, khỏe, hổ trợ nhanh chóng và nhiệt tình. homestay ở Cao Bằng đi bộ nhiều, khá mệt, nhưng không sao. tham quan ở hà giang hấp dẫn, thú vị.', '2020-10-28 00:00:00');
INSERT INTO `booking_db`.`review_tours` (`id_user`, `id_tour`, `score`, `content`, `create_time`) VALUES (123, 14, 10.0, 'Chương trình tốt, bữa ăn ngon - đầy đủ. Đang tham khảo thêm chương trình tour khác của iVIVU', ' 2020-10-21 00:00:00');
INSERT INTO `booking_db`.`review_tours` (`id_user`, `id_tour`, `score`, `content`, `create_time`) VALUES (124, 14, 10.0, 'HDV tuyệt vời, xe hơi cũ, tài xế chạy không an toàn, không thân thiện. Còn lại rất tốt từ khách sạn đến ăn uống. Bạn tư vấn viên tuyệt vời, hỗ trợ rất nhiệt tình.', '2020-10-14 00:00:00');
INSERT INTO `booking_db`.`review_tours` (`id_user`, `id_tour`, `score`, `content`, `create_time`) VALUES (125, 14, 8.0, 'Tour tốt, ăn uống ok, HDV nhiệt tình, chỉ riêng khu vực ở Ba Bể dơ, tour nên thay đổi địa điểm thì sẽ tốt hơn. Nhân viên tư vấn cũng rất nhiệt tình.', '2020-10-14 00:00:00');
