INSERT INTO `booking_db`.`review_tours` (`id_user`, `id_tour`, `score`, `content`, `create_time`) VALUES (130, 16, 10.0, 'Đã sử dụng rất nhiều sản phẩm của ivivu, lần nào cũng mang lại cảm giác hài lòng về dịch vụ lẫn cách phục vụ, tiếp tục chọn lựa ivivu nếu có kế hoạch tham quan du lịch tới đây', '2020-11-22 00:00:00');
INSERT INTO `booking_db`.`review_tours` (`id_user`, `id_tour`, `score`, `content`, `create_time`) VALUES (131, 16, 8.0, 'Bộ phân nhân viên hổ trợ tôt, dịch vụ hổ trợ thanh toán tiện lợi Chất lượng tour ổn so với giá, nhưng nếu khách lớn tuổi tham gia thì hơi mệt vì tuyến đường đèo trong quá trình di chuyển, mong ivivu sẽ có những chương trình phù hợp hơn', '2020-11-20 00:00:00');
