START TRANSACTION;
USE `booking_db`;
INSERT INTO `booking_db`.`hotels` (`id_hotel`, `id_location`, `id_area`, `id_utility`, `id_theme_hotel`, `id_type_of_hotel`, `title`, `star_rating`, `description`, `term_checkin`, `term_checkout`, `term_transportation`, `term_receiving_room`, `term_entertainment`, `term_surcharge`, `term_note`, `image_1`, `image_2`, `image_3`, `image_4`, `image_5`, `video_linked`, `score`, `number_of_reviews`) VALUES (1, 1, 21, 128, 2, 3, 'Khách sạn FLC Luxury Quy Nhơn', 5, 'Khách sạn FLC Luxury Quy Nhơn gồm 323 căn hộ với các tiện nghi đạt chuẩn 5 sao, tọa lạc tại xã Nhơn Lý, thành phố Quy Nhơn, tỉnh Bình Đình ôm trọn 3km bờ biển cùng Eo Gió vốn được mệnh danh là nơi ngắm hoàng hôn đẹp nhất Việt Nam, khách sạn nằm ở vị trí trung tâm nối các điểm du lịch nổi tiếng của Quy Nhơn như Đảo Kỳ Co, Cồn Chim, Bãi biển Nhơn Hội, Cát Tiến…\\nĐến với FLC Luxury Quy Nhơn quý khách không nên bỏ qua Công viên động vật hoang dã đầu tiên tại Quy Nhơn, với diện tích lên đến 129ha, FLC Zoo Safari hiện tại đã và đang bảo tồn các loài động vật quý hiếm.\\nKhách sạn FLC cách sân bay Phù Cát 40 phút và để đi đến trung tâm thành phố chỉ với 15 phút lái xe.\\nSố lượng phòng: 323 phòng.', '14:00', '12:00', '- Cách sân bay Phù Cát 35km, khoảng 40 phút lái xe\\n- Cách trung tâm thành phố Quy Nhơn 19km.', 'Trong trường hợp đặt phòng có đặt đón sân bay, quý khách vui lòng ra khu vực lấy hành lý, sẽ thấy quầy FLC & nhân viên đại diện của Khách sạn chờ khách.\\nLưu ý: Dịch vụ đón tiễn sân bay phải đặt trước ít nhất 48 tiếng, trong trường hợp dưới 48 tiếng sẽ phục vụ theo tình trạng xe tại thời điểm (có thu phí)\\nQuý khách vui lòng xuất trình Hộ chiếu/CMND và Phiếu nhận phòng của iVIVU.com khi nhận phòng tại khách sạn.\\n(Khách sạn sẽ yêu cầu Quý khách khoản tiền đặt cọc khoảng 1,000,000VND/phòng/đêm tại thời điểm nhận phòng. Khuyến khích đặt cọc bằng tiền mặt hoặc thẻ tín dụng credit card)', NULL, '- Trẻ em dưới 6 tuổi miễn phí ngủ cùng bố mẹ (khách sạn không hỗ trợ cũi)\\n- Trẻ em từ 6 đến 11 tuổi phụ thu 220.000VND/trẻ/đêm ngủ cùng bố mẹ, nếu có 2 trẻ phụ thu thêm giường phụ 550.000VND/giường/đêm\\n- Trẻ em từ 12 tuổi trở lên tính như người lớn\\n*Extra bed: 880.000VND/đêm\\nChính sách bữa ăn bắt buộc:\\n+ Giai đoạn cao điểm 15/05-15/08 yêu cầu đặt thêm 01 bữa ăn/booking\\n+ Giai đoạn Lễ 30/04-03/05, 02/09, 24/12, 31/12-03/01 yêu cầu đặt thêm 01 bữa ăn mỗi ngày', 'Lịch trình Shuttle Bus\\n- Quý khách vui lòng đăng ký trước ít nhất 2 tiếng khi sử dụng shuttle bus đi thành phố hoặc Eo Gió tại quầy lễ tân hoặc gọi theo số: (+84) 256 628 8888. Chỗ trống tùy thuộc vào thời điểm sử dụng.\\n', 'flc-quy-nhon-hotel-1-1290x860-800x450.jpg', 'flc-quy-nhon-hotel-1290x860-800x450.jpg', 'khach-san-flc-luxury-quy-nhon-1-800x450.jpg', 'khach-san-flc-luxury-quy-nhon-4-800x450.jpg', 'khach-san-flc-luxury-quy-nhon-800x450.jpg', NULL, 0.0, 0);

