CREATE DEFINER = CURRENT_USER TRIGGER `booking_db`.`hotels_BEFORE_INSERT` BEFORE INSERT ON `hotels` FOR EACH ROW
BEGIN
	declare rowcount int;
    
    select count(*)
    into rowcount
    from areas
    where areas.id_area = new.id_area and areas.number_of_hotels is null;
    
	if rowcount > 0 then
        update areas
		set areas.number_of_hotels = 1
		where new.id_area = areas.id_area;
    else
        update areas
		set areas.number_of_hotels = areas.number_of_hotels+1
		where new.id_area = areas.id_area;
    end if; 
    
    select count(*)
    into rowcount
    from cities
    where ( select id_city from locations where new.id_location = locations.id_location) = cities.id_city
			and cities.number_of_hotels is null;
    
    if rowcount > 0 then
        update cities
		set cities.number_of_hotels = 1
